import { Controller, Post, Body, Get, Param } from '@nestjs/common';
import { PaymentService } from './payment.service';

@Controller('payments')
export class PaymentController {
  constructor(private readonly paymentService: PaymentService) {}

  // 결제 요청 시작
  @Post()
  async initiatePayment(@Body() dto: {
    orderId: string;
    method: string;
    amount: number;
  }) {
    return this.paymentService.initiate(dto);
  }

  // PG사 결제 완료 웹훅 (카카오페이, 네이버페이 등)
  @Post('webhook/:provider')
  async handleWebhook(
    @Param('provider') provider: string,
    @Body() payload: any,
  ) {
    return this.paymentService.handleWebhook(provider, payload);
  }

  // 결제 내역 조회
  @Get(':id')
  async getPayment(@Param('id') id: string) {
    return this.paymentService.findById(id);
  }

  // 환불 요청
  @Post(':id/refund')
  async refund(@Param('id') id: string, @Body() dto: { reason: string }) {
    return this.paymentService.refund(id, dto.reason);
  }
}

